import React, { useState } from "react";
import axios from "axios";

const RegisterForm = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    role: "", // New state for role
    doctorId: "", // New state for doctor's ID
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Validate doctor's ID if the role is 'doctor'
      if (
        formData.role === "doctor" &&
        !/^RGTH\d{2}$/.test(formData.doctorId)
      ) {
        alert(
          "Doctor ID format is invalid. It should start with 'RGTH' followed by two numbers."
        );
        return;
      }

      const response = await axios.post(
        "http://localhost:3000/register",
        formData,
        {
          headers: {
            "Content-Type": "application/json",
          },
          withCredentials: true,
        }
      );
      console.log("Registration successful:", response.data);
      // Redirect or show success message
    } catch (error) {
      console.error("Registration failed:", error.response.data);
      // Show error message
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="email"
        name="email"
        value={formData.email}
        onChange={handleChange}
        placeholder="Email"
        required
      />
      <input
        type="password"
        name="password"
        value={formData.password}
        onChange={handleChange}
        placeholder="Password"
        required
      />
      <select name="role" value={formData.role} onChange={handleChange}>
        <option value="">Select Role</option>
        <option value="doctor">Doctor</option>
        <option value="patient">Patient</option>
      </select>
      {formData.role === "doctor" && (
        <input
          type="text"
          name="doctorId"
          value={formData.doctorId}
          onChange={handleChange}
          placeholder="Doctor ID"
          required
        />
      )}
      <button type="submit">Register</button>
    </form>
  );
};

export default RegisterForm;
